CREATE DATABASE IF NOT EXISTS GymNest;
use GymNest;

DROP TABLE IF EXISTS exercises_app ;
CREATE TABLE IF NOT EXISTS exercises_app (
  `id` BIGINT NOT NULL PRIMARY KEY,
  `name_of_exercise` VARCHAR(1024),
  `Desc` VARCHAR(1024) NULL,
  `Type` VARCHAR(1024),
  `BodyPart` VARCHAR(1024),
  `Equipment` VARCHAR(1024),
  `Level` VARCHAR(1024),
  `Rating` DOUBLE NULL,
  `RatingDesc` VARCHAR(1024) NULL
);


-- Table for Users
CREATE TABLE IF NOT EXISTS Users (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    fname VARCHAR(255) NOT NULL,
    lname VARCHAR(255) NOT NULL,
    password VARCHAR(255) NOT NULL,
    dateOfBirth  VARCHAR(10),
    gender  VARCHAR(1024),
    level VARCHAR(1024),
    weight DECIMAL(5,2),
    height DECIMAL(5,2),
    `admin` TINYINT(1) DEFAULT 1 -- Boolean field
);


-- Table for BodyPartGroup
CREATE TABLE IF NOT EXISTS  BodyPartGroup (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    groupName VARCHAR(255) NOT NULL UNIQUE
);
VALUES ('Upper Body'), ('Lower Body'), ('Full Body'), ('Core'),('Legs');

-- Table for BodyPart
CREATE TABLE IF NOT EXISTS BodyPart (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    name VARCHAR(255) NOT NULL UNIQUE,
    imageUrl VARCHAR(2083),
    BodyPartGroupId INT,
    FOREIGN KEY (BodyPartGroupId) REFERENCES BodyPartGroup(id) ON DELETE CASCADE
);

SELECT DISTINCT BodyPart
FROM exercises_app
WHERE BodyPart IS NOT NULL AND BodyPart != '';

-- Table for Equipment
CREATE  TABLE IF NOT EXISTS Equipment (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    name VARCHAR(255) NOT NULL UNIQUE,
    imageUrl VARCHAR(2083)

);
SELECT DISTINCT Equipment
FROM exercises_app
WHERE Equipment IS NOT NULL AND Equipment != ''
ON DUPLICATE KEY UPDATE name = name;  -- Do nothing if the name already exists


-- Table for Exercise
CREATE TABLE IF NOT EXISTS Exercise (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    name_of_exercise TEXT,
    level TEXT,
    `Desc` TEXT,
    `Type` TEXT,
    linkForVideo TEXT,
    linkForImage TEXT,
    equipmentId INT,
    BodyPartEffectedId INT,
    FOREIGN KEY (equipmentId) REFERENCES Equipment(id) ON DELETE SET NULL,
    FOREIGN KEY (BodyPartEffectedId) REFERENCES BodyPart(id) ON DELETE CASCADE
);



SELECT
    `name_of_exercise`,
    `Desc`,
    `Type`,
    `level`,
    (SELECT id FROM Equipment WHERE name = exercises_app.Equipment)
FROM exercises_app
WHERE
`Desc` IS NOT NULL AND `Type` IS NOT NULL AND `name_of_exercise` IS NOT NULL AND `level` IS NOT NULL ;

ALTER TABLE Exercise ADD FULLTEXT(`name_of_exercise`,`Desc`,`Type`,`level`);

-- Table for Recommendations
CREATE TABLE IF NOT EXISTS Recommendations (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    userId INT,
    equipmentId INT,
    level VARCHAR(1024),
    sets INT DEFAULT 3,
    reps INT DEFAULT 12,
    exerciseId INT UNIQUE,
    FOREIGN KEY (exerciseId) REFERENCES Exercise(id) ON DELETE SET NULL,
    FOREIGN KEY (userId) REFERENCES Users(id) ON DELETE CASCADE,
    FOREIGN KEY (equipmentId) REFERENCES Equipment(id) ON DELETE SET NULL
);



-- Table for ProgressTracking
CREATE TABLE IF NOT EXISTS ProgressTracking (
    id INT AUTO_INCREMENT NOT NULL PRIMARY KEY,
    userId INT,
    exerciseId INT UNIQUE,
    dateCompleted DATE,
    setsCompleted INT,
    repsCompleted INT,
    duration TIME,
    caloriesBurned DECIMAL(10,2),
    FOREIGN KEY (userId) REFERENCES Users(id) ON DELETE CASCADE,
    FOREIGN KEY (exerciseId) REFERENCES Exercise(id) ON DELETE CASCADE
);
